import React, { Component } from 'react'
import Subcard from "./Subcard";
import Carousel from "react-spring-3d-carousel";
import uuidv4 from "uuid";
import { config } from "react-spring";

let slides = [
    {
    key: uuidv4(),
      content: <img src="https://picsum.photos/800/801/?random" alt="1" onClick= {this} />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/800/802/?random" alt="2" />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/600/803/?random" alt="3" />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/800/500/?random" alt="4" />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/800/804/?random" alt="5" />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/500/800/?random" alt="6" />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/800/600/?random" alt="7" />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/805/800/?random" alt="8" />
    }
  ];

export default class Event extends Component {
    state = {
        goToSlide: 0,
        offsetRadius: 2,
        showNavigation: true,
        config: config.gentle
      };

      onChangeInput = (e: React.ChangeEvent<HTMLInputElement>) => {
        this.setState({
          [e.target.name]: parseInt(e.target.value, ) || 0
        });
      };

    render() {
        return (
            <div className="container_event">
                <h1>GAMEX</h1>
                <div className="nav_slide">
                    <button style={{backgroundColor:'#000000',width:'100px',height:'40px',color:'white',borderRadius:'10px',border:'none',cursor:'pointer',marginTop:'100px'}} >CS:GO</button>
                </div>
                <div className="sliders" style={{ width: "80%", height: "500px",marginTop:'50px',alignItems:'center' /*margin: "0 auto"*/}}>
                <Carousel
                    slides={slides}
                    goToSlide={this.state.goToSlide}
                    offsetRadius={this.state.offsetRadius}
                    showNavigation={this.state.showNavigation}
                    animationConfig={this.state.config}
                />
                    <div style={{ margin: "0 auto",
                    marginTop: "7rem",
                    width: "50%",
                    display: "flex",
                    justifyContent: "space-around"}}>

                    <div>
                        <label>Go to slide: </label>
                        <input name="goToSlide" onChange={this.onChangeInput} />
                    </div>
                    <div>
                        <label>Offset Radius: </label>
                        <input name="offsetRadius" onChange={this.onChangeInput} />
                    </div>

                  </div>


                </div>

            </div>
        )
    }
}
